mapper.select:
SELECT vdas.vin, vdas.vid, veh.license_plate as licensePlate, sd.name AS vehTypeName, svm.veh_model_name AS vehModelName, svm.model_notice_id AS modelNoticeId, stmu.term_part_firmware_number AS termPartFirmwareNumber, stmu.term_bar_code AS termBarCode, stmu.serial_number AS serialNumber, smu.name AS manuUnitName, sun.name AS useUnitName, area.name AS areaName, DATE_FORMAT(veh.first_reg,'%Y-%m-%d %h:%i:%s') AS firstReg, veh.sale_time AS saleTime, stmu.iccid, DATE_FORMAT(if(vdas.typeFlag != 4, vdas.reportDate ,vter.serverTime),'%Y-%m-%d %h:%i:%s') as reportDate, vdas.speedNumber, vdas.mileageNumber, vdas.lngLatNumber, vter.timeNumber, vdas.voltageNumber, vdas.electricNumber, vdas.socNumber, vdas.typeFlag FROM ( SELECT vda.vin, vda.vid, vda.type as typeFlag, max(vda.report_date) as reportDate, sum( IF ( vda.type = 1, vda.abnormal_num, 0 ) ) AS speedNumber, sum( IF ( vda.type = 2, vda.abnormal_num, 0 ) ) AS mileageNumber, sum( IF ( vda.type = 3, vda.abnormal_num, 0 ) ) AS lngLatNumber, sum( IF ( vda.type = 5, vda.abnormal_num, 0 ) ) AS voltageNumber, sum( IF ( vda.type = 6, vda.abnormal_num, 0 ) ) AS electricNumber, sum( IF ( vda.type = 7, vda.abnormal_num, 0 ) ) AS socNumber FROM ( SELECT vd.* FROM veh_dayreport_abnormal_situation vd WHERE 1 = 1
AND UNIX_TIMESTAMP(vd.report_date) >= UNIX_TIMESTAMP(#{startTime}) AND UNIX_TIMESTAMP(#{endTime}) >= UNIX_TIMESTAMP(vd.report_date)
) vda GROUP BY vda.vin ) vdas LEFT JOIN sys_vehicle veh ON veh.vin = vdas.vin LEFT JOIN sys_veh_model svm ON veh.veh_model_id = svm.id LEFT JOIN sys_dict sd ON svm.veh_model_type_id = sd.id AND sd.type = 2 LEFT JOIN sys_term_model_unit stmu ON veh.term_id = stmu.id LEFT JOIN sys_unit smu ON veh.manu_unit_id = smu.id LEFT JOIN sys_unit sun ON veh.use_uint_id = sun.id LEFT JOIN sys_area area ON area.id = veh.sys_division_id LEFT JOIN ( select count(id) as timeNumber,server_time as serverTime,vin,vid from veh_time_exception_record WHERE 1 = 1
AND UNIX_TIMESTAMP(server_time) >= UNIX_TIMESTAMP(#{startTime}) AND UNIX_TIMESTAMP(#{endTime}) >= UNIX_TIMESTAMP(server_time)
) vter ON veh.vin = vter.vin
AND veh.use_uint_id = #{useUnitName}
AND veh.license_plate like CONCAT('%',#{licensePlate},'%')
AND veh.vin like CONCAT('%',#{vin},'%')
AND svm.veh_model_type_id = #{vehTypeName}
AND veh.veh_model_id = #{vehModelName}
AND veh.registerstatus = #{vehStage}
AND veh.sys_division_id = #{areaName}
AND veh.vin IN
#{item}
OR
AND
veh.license_plate IN
#{item}
/*GROUP BY veh.vin*/ ORDER BY vdas.reportDate DESC,veh.license_plate,veh.vin

SELECT vter.server_time as reportDate, vter.lng_lat as lngLat FROM veh_time_exception_record vter WHERE vid = #{vid}

    AND UNIX_TIMESTAMP(vter.server_time) >= UNIX_TIMESTAMP(#{startTime})
    AND UNIX_TIMESTAMP(#{endTime}) >= UNIX_TIMESTAMP(vter.server_time)
