mapper.select:
SELECT DATE_FORMAT(v_e.report_date, '%Y-%m-%d') AS reportDate, veh.license_plate AS licensePlate, veh.vin AS vin, s_d.name AS vehTypeId, s_v_m.veh_model_name AS vehModelName, s_v_m.model_notice_id AS modelNoticeId, s_t_m.term_part_firmware_numbers AS termPartFirmwareNumbers, t_u.term_bar_code AS termBarCode, t_u.term_vendor_code AS termVendorCode, su.name AS manuUnitId, s_u.name AS useUintId, s_a.name AS sysDivisionId, DATE_FORMAT(veh.entry_date,'%Y-%m-%d %h:%i:%s') AS entryDate, veh.sale_time AS saleTime, v_e.daily_active_total_time AS dailyActiveTotalTime, v_r.run_time_sum AS runTimeSum, v_r.run_times AS runTimes, v_r.run_km AS runKm, v_r.run_km_max AS runKmMax, v_m_c.last_end_mileage AS lastEndMileage, v_c.charge_times AS chargeTimes, v_c.charge_time_sum AS chargeTimeSum, v_c.charge_time_max AS chargeTimeMax, v_e.single_charge_max_mileage AS singleChargeMaxMileage, v_r.run_speed_max AS runSpeedMax, v_r.run_speed_avg AS runSpeedAvg, DATE_FORMAT(v_m_c.last_commit_time,'%Y-%m-%d %h:%i:%s') AS lastCommunTime FROM sys_vehicle AS veh LEFT JOIN veh_dayreport_expansion v_e ON veh.vin = v_e.vin LEFT JOIN sys_dict s_d ON veh.veh_type_id = s_d.id LEFT JOIN sys_veh_model s_v_m ON veh.veh_model_id = s_v_m.id LEFT JOIN sys_term_model_unit t_u ON veh.vin = t_u.vin LEFT JOIN sys_term_model s_t_m ON s_t_m.id = t_u.sys_term_model_id
LEFT JOIN sys_unit su ON veh.manu_unit_id = su.id LEFT JOIN sys_unit s_u ON veh.use_uint_id = s_u.id LEFT JOIN sys_area s_a ON veh.sys_division_id = s_a.id LEFT JOIN veh_dayreport_runstate v_r ON veh.uuid = v_r.vid LEFT JOIN veh_dayreport_mileage_check v_m_c ON veh.vin = v_m_c.vin LEFT JOIN veh_dayreport_chargestate v_c ON veh.uuid = v_c.vid
AND v_e.report_date >= #{startTime} AND v_e.report_date <= #{endTime}
AND veh.license_plate LIKE concat(concat('%',#{licensePlate}),'%')
AND veh.vin LIKE concat(concat('%',#{vin}),'%')
AND veh.vin IN
#{item}
OR
AND
veh.license_plate IN
#{item}
AND veh.veh_type_id = #{vehTypeId}
AND s_v_m.id = #{vehModelName}
AND s_u.id = #{useUintId}
AND s_a.id = #{sysDivisionId}
GROUP BY v_e.report_date,veh.license_plate ORDER BY v_m_c.last_commit_time,v_e.report_date,veh.license_plate,veh.vin,v_e.daily_active_total_time DESC
